import React, { useState, useEffect } from "react";
import "./App.css"; // Assurez-vous de créer ce fichier CSS pour gérer les styles.

const App = () => {
  const [posts, setPosts] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    const rssFeedUrl = "https://www.lemonde.fr/rss/une.xml";

    const fetchRSS = async () => {
      try {
        const response = await fetch(
          `https://api.rss2json.com/v1/api.json?rss_url=${encodeURIComponent(
            rssFeedUrl
          )}`
        );
        const data = await response.json();
        if (data.items) {
          setPosts(data.items);
        }
      } catch (error) {
        console.error("Erreur lors de la récupération du flux RSS :", error);
      }
    };

    fetchRSS();
  }, []);

  const handleScroll = (e) => {
    if (e.deltaY > 0 && currentIndex < posts.length - 1) {
      setCurrentIndex((prevIndex) => prevIndex + 1);
    } else if (e.deltaY < 0 && currentIndex > 0) {
      setCurrentIndex((prevIndex) => prevIndex - 1);
    }
  };

  return (
    <div 
      className="app-container" 
      onWheel={handleScroll} 
      style={{ height: "100vh", overflow: "hidden" }}
    >
      {posts.length > 0 && (
        <div 
          className="post-container" 
          style={{ height: "100vh", display: "flex", justifyContent: "center", alignItems: "center", padding: "1rem" }}
        >
          <div className="post-content" style={{ textAlign: "center" }}>
            <h2 className="post-title" style={{ fontSize: "1.5rem", fontWeight: "bold", marginBottom: "1rem" }}>
              {posts[currentIndex].title}
            </h2>
            <p className="post-snippet" style={{ fontSize: "1rem", marginBottom: "1.5rem" }}>
              {posts[currentIndex].contentSnippet}
            </p>
            <a
              href={posts[currentIndex].link}
              target="_blank"
              rel="noopener noreferrer"
              className="post-link"
              style={{ color: "#1d4ed8", textDecoration: "underline" }}
            >
              Lire plus
            </a>
          </div>
        </div>
      )}
    </div>
  );
};

export default App;
